mapper.select:
SELECT * FROM tb_jt_board where 1
   AND gubun= #{keyGubun}

ORDER BY regdate DESC, seq DESC 
LIMIT ${pg_start} , ${pg_rows}

mapper.update:
        UPDATE tb_jt_board 
		SET gubun = #{gubun} 
	        ,main_yn = #{mainYn} 
	        ,img_seq = #{imgSeq} 
	        ,file1_seq = #{file1Seq} 
	        ,file2_seq = #{file2Seq}
	        ,file3_seq = #{file3Seq}
	        ,file4_seq = #{file4Seq}
	        ,lang = #{lang}
	        ,isnt_yn = #{isntYn}
	        ,title_kr = #{titleKr}
	        ,title_en = #{titleEn}
	        ,title_cn = #{titleCn}
	        ,contents_kr = #{contentsKr}
	        ,contents_en = #{contentsEn}
	        ,contents_cn = #{contentsCn}
	        ,adm_id = #{admId}

        WHERE seq = #{seq}

        UPDATE tb_jt_board 
		 SET img_seq = #{keyValue} 
	     SET file1_seq = #{keyValue}  
	     SET file2_seq = #{keyValue}  
	     SET file3_seq = #{keyValue}  
	     SET file4_seq = #{keyValue}  
        WHERE seq = #{bseq}
