mapper.insert:
INSERT INTO tb_files set pseq = #{pseq}
,gubun = #{gubun}
,filename = #{filename}
,save_filename = #{save_filename}
,file_size = #{file_size}
,regdate = now()

mapper.select:
SELECT * FROM tb_files where 1
and pseq = #{pseq}
and gubun = #{gubun}
order by regdate desc

SELECT count(*) as c FROM tb_files where 1
and pseq = #{pseq}
order by regdate desc
